abstract contract Staking is ERC20 {
    uint8 private constant _NOT_ENTERED = 1;
    uint8 private constant _ENTERED = 2;
    uint8 private _status;
    uint8 internal isOpenedVote; //voting controller
    uint8 internal earlyElection;
    uint8 internal treasury;
    uint8 internal typeofVote; //Type0: general voting & parliamentary vote / Type1: presidintial election / Type2: judge vote 
    //unix timestamp values
    uint8 internal oneMinute=60;
    uint16 internal oneHour= 3600;
    uint32 internal oneDay=86400;
    uint32 internal oneMonth=2592000;
    uint32 internal oneYear=31536000;
    //
    uint64 internal startTime; // voting start time
    uint64 internal endTime; //voting end time
    uint64 internal counter; //counter for voting
    uint256 internal judgeElectionTime;
    uint256 internal electionTime;
    uint256 internal _usedFunds;
    uint256 internal numberofChoices= 1; //number of options in voting
    uint256 internal amount = 1 ether;
    address internal owner;
    address internal president; // President wallet address
    address internal _president;
    address[] internal candidateList; 
    address[] internal presidentList;
    address[] internal _judgecandiates;    
    address[] internal ministers;
    mapping (uint256 => uint256) internal _votenumber; // choice => number of votes
    mapping (uint256 => Voter) internal _votes; // voter id => vote 
    mapping (address => uint256) internal _stakers; 
    struct Voter {
        bool voted;  
        uint vote;   
    }
    IERC20 public immutable DAOTOKEN = IERC20(address(this));

    constructor() {
        _status = _NOT_ENTERED;
    }

    modifier nonReentrant() {
        // On the first call to nonReentrant, _notEntered will be true
        require(_status != _ENTERED, "ReentrancyGuard: reentrant call");

        // Any calls to nonReentrant after this point will fail
        _status = _ENTERED;

        _;

        // By storing the original value once again, a refund is triggered (see
        // https://eips.ethereum.org/EIPS/eip-2200)
        _status = _NOT_ENTERED;
    } 

    function stake() external nonReentrant{
        require(_stakers[_msgSender()]==0,"You're already staked.");
        DAOTOKEN.transferFrom(_msgSender(),address(this),amount);
        _stakers[_msgSender()]= block.timestamp;
    }
    function claim() external nonReentrant{
        require(_stakers[_msgSender()]!=0,"You did not stake.");
        require(block.timestamp-(_stakers[_msgSender()])>=31536000);
        require(DAOTOKEN.balanceOf(address(this))>=amount,"Insufficient balance.");
        _stakers[_msgSender()]=0;
        DAOTOKEN.transfer(_msgSender(),amount);


    }
    function isStaked(address staker) public view returns(bool result){
        result=_stakers[staker]>0;
    }
}




abstract contract Ballot is Staking{


    constructor()

    {
        isOpenedVote=0;
    }
    modifier votingTime() {
        require(block.timestamp>=startTime && block.timestamp<=endTime, "Voting is close."); //modifying for voting periods
        _;
    }

    modifier isopenedVote() {
        require(isOpenedVote==0,"There is already a vote");
        _;
    }

    //function triggering "startVote" function for Type0 voting. 
    //Only token holders can use.
    function startVote(uint8 _time, uint8 _timeType, uint _choiceNumber) external { 
        DAOTOKEN.transferFrom(_msgSender(),address(this),.1 ether);
        startVoting(_time,_timeType,_choiceNumber,0);
    }
    //params:
    //voteTime(with timeType): Voting time
    //timeType: 0:minute, 1: hour 2: day, 3: month 4: year
    //choiceNumber: number of choices 
    //voteType is typeofVote 
    function startVoting(uint8 voteTime, uint8 timeType, uint choiceNumber,uint8 voteType) internal isopenedVote{
        require(timeType<=4 && timeType>=0,"Wrong time type.");
        require(DAOTOKEN.balanceOf(_msgSender())>=0,"You can not start voting.");
        numberofChoices = choiceNumber;
        startTime=uint64(block.timestamp);
        if(timeType==0) endTime= oneMinute*(voteTime)+(startTime);
        else if(timeType==1) endTime= oneHour*(voteTime)+(startTime);
        else if(timeType==2) endTime = oneDay*(voteTime)+(startTime);
        else if(timeType==3) endTime = oneMonth*(voteTime)+(startTime);
        else endTime = oneYear*(voteTime)+(startTime);
        if(counter>0){
        for(uint i=0; i<=counter;i++){
            delete _votes[i];
        }
        for(uint k=0; k<=numberofChoices;k++){
            delete _votenumber[k];
        }
        counter=0;
        }
        typeofVote=voteType;
        isOpenedVote=1;
    }


    //returns voting results
    function getWinner() public view returns(uint winner,uint votes) {
        require(block.timestamp>=endTime,"Voting is not over yet.");
        votes = 0;
        for (uint i = 1; i <=numberofChoices; i++) {
            if (_votenumber[i] > votes) {
                votes = _votenumber[i];
                winner = i;
            }
        }


    }

    function finishVoting() external {
        require(block.timestamp>=endTime && isOpenedVote==0,"You can not");
        isOpenedVote=0;
    }

    function getVote(uint choice) external votingTime{
        require(DAOTOKEN.balanceOf(_msgSender())>=0,"You are not authorized to vote.");
        require(choice>0 && choice<=numberofChoices,"Wrong Choice.");
        _votes[counter].voted = true;
        _votes[counter].vote = choice;
        _votenumber[choice] += 1;
        counter +=1;
    }


}






abstract contract Parliament is Ballot{

    constructor()
    {
        _usedFunds= 0;
        candidateList.push(address(_msgSender()));
        electionTime= block.timestamp+(oneMonth);
        earlyElection=0;
        treasury=0;
        _president=_msgSender();

    }
    modifier onlyPresident() {
        require(_msgSender()==_president,"You are not president");
        require(block.timestamp<=electionTime,"Your presidency is over.");
        _;
    }
    modifier onlyParliament() {
        require(ministers.length>0,"The government was not formed.");
        bool isMinister = false;
        for(uint i = 0 ; i < ministers.length; i++){
            if(address(_msgSender())==ministers[i]) isMinister=true;
        }
        require(_msgSender()==_president || isMinister,"You are not president or minister.");
        require(block.timestamp<=electionTime,"Your presidency is over.");
        _;
    }

   function candidate()external {
        for(uint i =0; i<presidentList.length;i++){
            if(address(_msgSender())==presidentList[i]){ revert("You can not be president a second time");}}
        require(isStaked(address(_msgSender())),"You are did not stake.");
        candidateList.push(address(_msgSender()));

   }

    function electionResult() external {
        require(typeofVote==1, "Do not found election");
        (uint id, )=getWinner();
        _president= candidateList[id];
        presidentList.push(_president);
        }
    function useFunds(uint256 amount) external onlyPresident{
        require(amount<=DAOTOKEN.balanceOf(address(this)));
        require(amount<=(DAOTOKEN.balanceOf(address(this))-(_usedFunds)),"Insufficient balance.");
        _usedFunds += amount;
        DAOTOKEN.transfer(_msgSender(), amount);

    }
    function appointMinister(address[] calldata _ministers) external onlyPresident {
        require(_ministers.length==4,"Wrong number of ministers.");
        for(uint i = 0 ; i <_ministers.length ; i++){
            ministers[i]=_ministers[i];
        }
    }
    function startParliamentaryVote(uint8 _time, uint8 _timeType, uint8 _choiceNumber) external onlyParliament {
        startVoting(_time,_timeType,_choiceNumber,0);
    }
}










abstract contract Judge is   Parliament{

    constructor()
    {

        judgeElectionTime= block.timestamp+(oneMonth);
        judge=_msgSender();

    }
    modifier electionPeriod() {
        uint nextTime= electionTime+(oneMonth);
        require(block.timestamp>=nextTime || earlyElection==0 || address(_msgSender())== judge, "Voting is close.");
        _;
    }
    modifier judgeElectionPeriod() {
        uint nextTime= electionTime+(oneMonth);
        require(block.timestamp>=nextTime, "Voting is close.");
        _;
    }
    modifier onlyJudge() {
        require(address(_msgSender())==judge, "You do not have permission to access this function.");
        _;
    }

   function election(uint8 voteTime, uint8 timeType ) external electionPeriod{
       if(electionTime<block.timestamp){ earlyElection=1;}
       uint candiates = candidateList.length;
       startVoting(voteTime, timeType, candiates,1);
   }
    function judgeElection(uint8 voteTime, uint8 timeType ) external judgeElectionPeriod{
       uint candiates = _judgecandiates.length;
       startVoting(voteTime, timeType, candiates,2);
   }
    function judgeCandidate()external {
        require(isStaked(address(_msgSender())),"You are did not stake.");
        _judgecandiates.push(address(_msgSender()));
       } 
   function judgeElectionResult() public {
        require(typeofVote==2, "Do not found election");
        (uint id,)=getWinner();
        judge= _judgecandiates[id];
        }
    function pauseTransactions()external onlyJudge{
        if(isPause==0){
            isPause=1;
        }
        else{
            isPause=0;
        }
    }
}





contract DAO is Judge{



    constructor() ERC20("DAOTOKEN", "DAO",2000000 ether) //Name & symbol
    {
        owner= _msgSender();
    }
    modifier onlyOwner(){
        require(_msgSender()==owner,"You are not owner.");
        _;
    }
    function mint(address recipient_, uint256 amount_) external onlyOwner {
        _mint(recipient_, amount_);
    }


}

